import React, { useState, useEffect } from 'react';
import { HashRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Home from './pages/Home';
import Login from './components/Login';
import UserRegister from './components/UserRegister';
import EnterpriseRegister from './components/EnterpriseRegister';
import UserDashboard from './components/UserDashboard';
import EnterpriseDashboard from './components/EnterpriseDashboard';
import ProtectedRoute from './components/ProtectedRoute';

function App() {
    const [username, setUsername] = useState('');
    const [role, setRole] = useState('');

    useEffect(() => {
        const storedUsername = localStorage.getItem('username');
        const storedRole = localStorage.getItem('role');
        if (storedUsername && storedRole) {
            setUsername(storedUsername);
            setRole(storedRole);
        }
    }, []);

    const isAuthenticated = username !== '';

    const handleLogout = () => {
        localStorage.removeItem('username');
        localStorage.removeItem('role');
        setUsername('');
        setRole('');
    };

    return (
        <Router>
            <div className="App">
                <header className="flex justify-between p-4 bg-gray-800 text-white">
                    <h1 className="text-2xl">My App</h1>
                    {username && (
                        <div>
                            <span>Welcome, {username}</span>
                            <button onClick={handleLogout} className="ml-4 bg-red-500 text-white px-2 py-1 rounded">Logout</button>
                        </div>
                    )}
                </header>
                <Routes>
                    <Route exact path="/" element={<Home />} />
                    <Route path="/login" element={<Login setUsername={setUsername} setRole={setRole} />} />
                    <Route path="/register/user" element={<UserRegister />} />
                    <Route path="/register/enterprise" element={<EnterpriseRegister />} />
                    <Route 
                        path="/dashboard/user" 
                        element={
                            <ProtectedRoute isAuthenticated={isAuthenticated}>
                                <UserDashboard username={username} />
                            </ProtectedRoute>
                        } 
                    />
                    <Route 
                        path="/dashboard/enterprise" 
                        element={
                            <ProtectedRoute isAuthenticated={isAuthenticated}>
                                <EnterpriseDashboard username={username} />
                            </ProtectedRoute>
                        } 
                    />
                    <Route path="*" element={<Navigate to="/" />} />
                </Routes>
            </div>
        </Router>
    );
}

export default App;
