import React, { useState, useEffect } from 'react';
import { HashRouter as Router, Route, Routes, Navigate, Link } from 'react-router-dom';
import Home from './pages/Home';
import Login from './components/Login';
import Register from './components/Register';
import UserDashboard from './components/UserDashboard';
import EnterpriseDashboard from './components/EnterpriseDashboard';
import ProtectedRoute from './components/ProtectedRoute';

function App() {
    const [username, setUsername] = useState('');
    const [role, setRole] = useState('');
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        const storedUsername = localStorage.getItem('username');
        const storedRole = localStorage.getItem('role');
        if (storedUsername && storedRole) {
            setUsername(storedUsername);
            setRole(storedRole);
        }
        setIsLoading(false);
    }, []);

    const isAuthenticated = username !== '';

    const handleLogout = () => {
        localStorage.removeItem('username');
        localStorage.removeItem('role');
        setUsername('');
        setRole('');
    };

    return (
        <Router>
            <div className="App">
                {/* Navbar */}
                <header className="bg-gray-800 text-white">
                    <div className="container mx-auto flex justify-between items-center py-4 px-8">
                        {/* Logo */}
                        <Link to="/" className="text-2xl font-bold">
                            LOGO
                        </Link>

                        {/* Navigation Links */}
                        <nav className="space-x-8">
                            <Link to="/" className="hover:text-gray-400">Home</Link>
                            <Link to="/about" className="hover:text-gray-400">About</Link>
                            <Link to="/contact" className="hover:text-gray-400">Contact Us</Link>
                        </nav>

                        {/* User Info & Logout */}
                        {isAuthenticated && (
                            <div className="flex items-center space-x-4">
                                <span>Welcome, {username}</span>
                                <button onClick={handleLogout} className="bg-red-500 hover:bg-red-600 text-white px-4 py-2 rounded">Logout</button>
                            </div>
                        )}
                    </div>
                </header>

                {/* Main Content */}
                <Routes>
                    <Route exact path="/" element={<Home />} />
                    <Route path="/login" element={<Login setUsername={setUsername} setRole={setRole} />} />
                    <Route path="/register" element={<Register />} />
                    <Route 
                        path="/dashboard/user" 
                        element={
                            <ProtectedRoute isAuthenticated={isAuthenticated} isLoading={isLoading}>
                                <UserDashboard username={username} />
                            </ProtectedRoute>
                        } 
                    />
                    <Route 
                        path="/dashboard/enterprise" 
                        element={
                            <ProtectedRoute isAuthenticated={isAuthenticated} isLoading={isLoading}>
                                <EnterpriseDashboard username={username} />
                            </ProtectedRoute>
                        } 
                    />
                    <Route path="*" element={<Navigate to="/" />} />
                </Routes>
            </div>
        </Router>
    );
}

export default App;
