import React, { useState } from 'react';
import Navbar from '../components/Navbar';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

const Login = ({ setUsername, setRole }) => {
    const [username, setUsernameLocal] = useState('');
    const [password, setPassword] = useState('');
    const [role, setRoleLocal] = useState('user');
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const formData = new FormData();
            formData.append('username', username);
            formData.append('password', password);
            formData.append('scope', role === 'enterprise' ? 'enterprise' : '');

            const response = await api.login(formData);

            localStorage.setItem('username', username);
            localStorage.setItem('role', role);

            setUsername(username);
            setRole(role);

            if (role === 'enterprise') {
                navigate('/dashboard/enterprise');
            } else {
                navigate('/dashboard/user');
            }
        } catch (error) {
            console.error("Error message:", error.message);
            alert('Error logging in');
        }
    };

    return (
        <div>
            <Navbar />
            <div className="container mx-auto mt-20">
                <form onSubmit={handleSubmit} className="max-w-md mx-auto bg-white p-6 rounded shadow">
                    <h2 className="text-2xl font-bold mb-4">Login</h2>
                    <input
                        type="text"
                        placeholder="Username"
                        value={username}
                        onChange={(e) => setUsernameLocal(e.target.value)}
                        className="w-full p-2 mb-4 border rounded"
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        className="w-full p-2 mb-4 border rounded"
                    />
                    <select value={role} onChange={(e) => setRoleLocal(e.target.value)} className="w-full p-2 mb-4 border rounded">
                        <option value="user">User</option>
                        <option value="enterprise">Enterprise</option>
                    </select>
                    <button type="submit" className="w-full bg-blue-500 text-white p-2 rounded">Login</button>
                </form>
            </div>
        </div>
    );
};

export default Login;
